import { useRef, useState } from 'react';
import './App.css';

function App() {

  const cityRef = useRef()
  const [weatherData, setWeatherData] = useState([])
  const [cityOptions, setCityOptions] = useState([])

  function handleInputChange(){
    fetch(`http://api.geonames.org/searchJSON?q=${cityRef.current.value}&maxRows=100&username=mkskhjwssofwfjjcjw00&&`)  // lang=ru
      .then(data => data.json())
      .then(data => {
        const arrayCities = data.geonames
        .filter(city => city.name.toLowerCase().includes(cityRef.current.value.toLowerCase()))
        setCityOptions(arrayCities)
      })
  }

  function handleSearchClick(item){
    fetch(`https://api.weatherbit.io/v2.0/current?lat=${item.lat}&lon=${item.lng}&key=097dd5fffd324ebb875e47ef3fed2fa1`)
      .then(response  => response.json())
      .then(response  => setWeatherData(response.data))
      .catch(error => alert(error))
  }

  return (
    <div className="App">
      <form>
        <input 
          ref={cityRef} 
          type="text" 
          onChange={handleInputChange} 
        />
        {cityOptions.map(item => <ul onClick={()=>handleSearchClick(item)} style={{border: '1px solid black'}}>
          <h3>{item.name}</h3>
          <div style={{display:'flex', gap: '5px', textAlign: 'center'}}>
            <p>{item.adminName1},</p>
            <p>{item.countryName}</p>
          </div>
        </ul>)}
      </form>
      {/* <button onClick={handleSearchClick}>Search</button> */}
      {weatherData.map(item => <div>
        <h3>{item.city_name}</h3>
        <h1>{Math.round(item.temp)} Â°C</h1>
        <p>{item.ob_time}</p>
      </div>)}
    </div>
  );
}

export default App;
