import { useState, useEffect } from 'react';
import { BrowserRouter } from 'react-router-dom';
import './App.css';
import Context from './components/Context';
import Header from './Header/Header';
import Section from './Section/Section';

function App() {

  const [weatherData, setWeatherData] = useState([])
  const [nameCity, setNameCity] = useState('Madrid')
  const [timeCity, setTimeCity] = useState(null)

  useEffect(() => {
    fetch('https://api.weatherbit.io/v2.0/current?lat=40.4165&lon=-3.7026&key=097dd5fffd324ebb875e47ef3fed2fa1')
    .then(data => data.json())
    .then(data => {
        console.log(data.data[0], 'madrid')
        setWeatherData(data.data[0])
        setTimeout(()=>{
          console.log(weatherData)
        },2000)
    })
},[])

  const data = {
    weatherData,
    setWeatherData,
    nameCity,
    setNameCity,
    timeCity,
    setTimeCity
  }

  console.log(weatherData)

  return (
    <div className="App">
      <BrowserRouter>
        <Context.Provider value={data}>
          <Header />
          <Section />
        </Context.Provider>
      </BrowserRouter>
    </div>
  );
}

export default App;
