import React from 'react'
import { useState, useRef, useContext } from 'react'
import { useLocation, useNavigate } from 'react-router-dom'
import {uniqueBy} from '../../helpers'
import Context from '../../components/Context'
import closeIcons from '../../images/close_icon.png'
import './InputSearch.css'


export default function InputSearch() {

const cityRef = useRef()
const navigate = useNavigate()
const location = useLocation()
const setData = useContext(Context)
const [cityOptions, setCityOptions] = useState([])
const [isFound, setFound] = useState(false)
const [isActiveInput, setActiveInput] = useState(false)
const [isActiveDelete, setActiveDelete] = useState(true)
const [cityLocation, setCityLocation] = useState(null)
// const [counter, setCounter] = useState(0)

let timer

function handleInput(){
    clearTimeout(timer)
    timer = setTimeout(()=>{
        if(cityRef.current.value.length > 0){
            fetch(`http://api.geonames.org/searchJSON?name=${cityRef.current.value}&maxRows=100&username=mkskhjwssofwfjjcjw00&&&`)  // lang=ru
            .then(data => data.json())
            .then(data => {
                const filteredCities = data.geonames
                    .filter(filt => {
                        return filt.fclName === "city, village,..." 
                        && filt.fcodeName !== "section of populated place" 
                        && filt.lat && filt.lng 
                        && filt.name.toLowerCase().startsWith(cityRef.current.value.toLowerCase())
                    })
                    .sort((a,b)=> b.population - a.population)
                const uniqueFilteredCities = uniqueBy(filteredCities, (o1, o2) => o1.name === o2.name && o1.adminName1 === o2.adminName1 && o1.countryName === o2.countryName)
                uniqueFilteredCities.length === 0 ? setFound(true) : setFound(false)
                setCityOptions(uniqueFilteredCities)
                
            })
            .catch(() => alert('Error 1'))
            setActiveDelete(true)
        }
        else{
            setCityOptions([])
            setFound(false)
            setActiveDelete(false)
        }
    },1000)
    
    // fetch(`https://restcountries.com/v3.1/name/${cityOptions.countryName}?fields=flags`)
    // .then(data => data.json())
    // .then(data => console.log(data))
}

function handleSearchClick(item){

    if(location.pathname !== ''){
        fetch(`https://api.weatherbit.io/v2.0/current?lat=${item.lat}&lon=${item.lng}&key=097dd5fffd324ebb875e47ef3fed2fa1`)
        .then(response  => response.json())
        .then(response  => {
            setData.setWeatherData(response.data)
            setData.setNameCity(item.name)
            cityRef.current.value = ''
            setCityOptions([])
            setActiveDelete(false)
            navigate('/')
        })
        .catch(() => alert('Error 2'))
    }
    if(location.pathname === ''){
        fetch(`https://api.weatherbit.io/v2.0/current?lat=${item.lat}&lon=${item.lng}&key=097dd5fffd324ebb875e47ef3fed2fa1`)
        .then(response  => response.json())
        .then(response  => {
            setData.setWeatherData(response.data)
            setData.setNameCity(item.name)
            cityRef.current.value = ''
            setCityOptions([])
            setActiveDelete(false)
        })
        .catch(() => alert('Error 2'))
    }

    fetch(`http://api.timezonedb.com/v2.1/get-time-zone?key=WRFSMQ5HP6WJ&format=json&by=position&lat=${item.lat}&lng=${item.lng}`)
    .then(data => data.json())
    .then(data => {
        const dateTime = new Date(data.formatted)
        const hours = dateTime.getHours()
        const fullHours = hours < 10 ? `0${hours}` : hours
        const minutes = dateTime.getMinutes()
        const fullMinutes = minutes < 10 ? `0${minutes}` : minutes
        const time = `${fullHours}:${fullMinutes}`
        setData.setTimeCity(time)
    })
    .catch(() => alert('Error 3'))

}

const onKey = (e) => {
    if(e.key === 'Enter'){
        handleSearchClick(cityLocation)
    }
    // if(e.key === 'ArrowDown' && counter < cityOptions.length - 1){
    //     setCounter(prev => prev + 1)
    // }
    // if(e.key === 'ArrowUp' && counter > 0){
    //     setCounter(prev => prev - 1)
    // }
}

const onMouseEnter = (item) => {
    setCityLocation(item)
}

function handleBlur(){
    setTimeout(()=>{
        setActiveInput(false)
    },100)
}

const onClickDelete = () => {
    cityRef.current.value = ''
    setActiveDelete(false)
    setFound(false)
    setCityOptions([])
}

return (
<div className='search-wrapper'>
    <div className='input-search'>
        <input
            defaultValue='Madrid'
            className='input-search'
            placeholder='🔍︎ Search location'
            ref={cityRef} 
            type="text" 
            onInput={handleInput}
            onFocus={()=> setActiveInput(true)}
            onBlur={handleBlur}
            onKeyDown={onKey}
        />
        {isActiveDelete && <img onClick={onClickDelete} src={closeIcons} alt="delete_text" />}
    </div>
    <ul className={isActiveInput ? 'lists-wrapper active' : 'lists-wrapper'}>
        {isFound && <li className='not-found'>city is not found</li>}
        {cityOptions.map((item, index) => 
        <li 
            key={index} 
            onClick={()=>handleSearchClick(item)} 
            onMouseEnter={() => onMouseEnter(item)}
        >
            <h3>{item.name}</h3>
            <div className='city-details'>
                {item.adminName1 && <p>{item.adminName1},</p>}
                {item.countryName && <p>{item.countryName}</p>}
            </div>
        </li>)}
    </ul>
</div>
)
}
