import { useRef, useState } from 'react';
import './App.css';

function App() {

  const cityRef = useRef()
  const [weatherData, setWeatherData] = useState([])

  function handleClick(){
    fetch(`http://api.geonames.org/searchJSON?q=${cityRef.current.value}&maxRows=10&username=mkskhjwssofwfjjcjw00`)
      .then(data => data.json())
      .then(data => {
        const coordinates = data.geonames[0]
        if(coordinates){
          return fetch(`https://api.weatherbit.io/v2.0/current?lat=${coordinates.lat}&lon=${coordinates.lng}&key=097dd5fffd324ebb875e47ef3fed2fa1`)
          .then(response  => response .json())
          .then(response  => setWeatherData(response .data))
          .catch(error => alert(error))

        }
        else {
          alert('Неправильно вказали місто')
        }
      })
      .then(()=> cityRef = '')
  }

  return (
    <div className="App">
      <input ref={cityRef} type="text" />
      <button onClick={handleClick}>Search</button>
      {weatherData.map(item => <div>
        <h3>{item.city_name}</h3>
        <h1>{item.temp} °C</h1>
        <p>{item.ob_time}</p>
      </div>)}
    </div>
  );
}

export default App;
