import React from 'react'
import { useState, useContext, useEffect } from 'react'
import { useLocation, useNavigate } from 'react-router-dom'
import {uniqueBy} from '../../helper/filter-repeat'
import Context from '../../helper/Context'
import closeIcons from '../../images/close_icon.png'
import './InputSearch.css'


export default function InputSearch() {

const navigate = useNavigate()
const location = useLocation()
const setData = useContext(Context)
const [cityOptions, setCityOptions] = useState([])
const [isFound, setFound] = useState(false)
const [isActiveInput, setActiveInput] = useState(false)
const [cityLocation, setCityLocation] = useState(null)
const [inputValue, setInputValue] = useState('')
// const [counter, setCounter] = useState(0)
const isActiveDelete = inputValue.length > 0

useEffect(() => {
    fetch('https://api.weatherbit.io/v2.0/current?lat=40.4165&lon=-3.7026&key=c2d3c64087e54d018cae06444bf6a848')
    .then(data => data.json())
    .then(data => setData.setWeatherData(data.data))

    fetch('https://api.weatherbit.io/v2.0/forecast/daily?lat=40.4165&lon=-3.7026&key=c2d3c64087e54d018cae06444bf6a848')
    .then(data => data.json())
    .then(data => setData.setWeatherSixteenDays(data.data))

    fetch('https://api.weatherbit.io/v2.0/forecast/hourly?lat=40.4165&lon=-3.7026&key=c2d3c64087e54d018cae06444bf6a848')
    .then(data => data.json())
    .then(data => setData.setWeatherHourly(data.data))
},[])

useEffect(() => {
    const debounce = setTimeout(() => {
        if(inputValue.length > 0){
            fetch(`http://api.geonames.org/searchJSON?name=${inputValue}&maxRows=100&username=mkskhjwssofwfjjcjw00&&&`)  // lang=ru
            .then(data => data.json())
            .then(data => {
                console.log(data)
                // const filteredCities = data.geonames
                //     .filter(filt => {
                //         return filt.fclName === "city, village,..." 
                //         && filt.fcodeName !== "section of populated place" 
                //         && filt.lat && filt.lng 
                //         && filt.name.toLowerCase().startsWith(inputValue.toLowerCase())
                //     })
                //     .sort((a,b)=> b.population - a.population)
                // const uniqueFilteredCities = uniqueBy(filteredCities, (o1, o2) => o1.name === o2.name && o1.adminName1 === o2.adminName1 && o1.countryName === o2.countryName)
                // uniqueFilteredCities.length === 0 ? setFound(true) : setFound(false)
                // setCityOptions(uniqueFilteredCities)
            })
            .catch(() => alert('Error 1'))
        }
        else{
            setCityOptions([])
            setFound(false)
        }
    },1000)

    return () => clearTimeout(debounce)

},[inputValue])

function handleInput(e){
    setInputValue(e.target.value)
}

function handleSearchClick(item){

    if(location.pathname !== ''){
        fetch(`https://api.weatherbit.io/v2.0/current?lat=${item.lat}&lon=${item.lng}&key=c2d3c64087e54d018cae06444bf6a848`)
        .then(response  => response.json())
        .then(response  => {
            setData.setWeatherData(response.data)
            setData.setNameCity(item.name)
            setInputValue('')
            setCityOptions([])
            navigate('/')
        })
        .catch(() => alert('Error 2'))
    }
    else{
        fetch(`https://api.weatherbit.io/v2.0/current?lat=${item.lat}&lon=${item.lng}&key=c2d3c64087e54d018cae06444bf6a848`)
        .then(response  => response.json())
        .then(response  => {
            setData.setWeatherData(response.data)
            setData.setNameCity(item.name)
            setInputValue('')
            setCityOptions([])
        })
        .catch(() => alert('Error 2'))
    }

    fetch(`https://api.weatherbit.io/v2.0/forecast/daily?lat=${item.lat}&lon=${item.lng}&key=c2d3c64087e54d018cae06444bf6a848`)
        .then(data => data.json())
        .then(data => setData.setWeatherSixteenDays(data.data))

    fetch(`https://api.weatherbit.io/v2.0/forecast/hourly?lat=${item.lat}&lon=${item.lng}&key=c2d3c64087e54d018cae06444bf6a848`)
        .then(data => data.json())
        .then(data => setData.setWeatherHourly(data.data))
}

const onKey = (e) => {
    if(e.key === 'Enter'){
        handleSearchClick(cityLocation)
    }
    // if(e.key === 'ArrowDown' && counter < cityOptions.length - 1){
    //     setCounter(prev => prev + 1)
    // }
    // if(e.key === 'ArrowUp' && counter > 0){
    //     setCounter(prev => prev - 1)
    // }
}

const onMouseEnter = (item) => {
    setCityLocation(item)
}

function handleBlur(){
    setTimeout(()=>{
        setActiveInput(false)
    },100)
}

const onClickDelete = () => {
    setInputValue('')
    setFound(false)
    setCityOptions([])
}

return (
<div className='search-wrapper'>
    <div className='input-search'>
        <input
            value={inputValue}
            className='input-search'
            placeholder='🔍︎ Search location'
            type="text" 
            onInput={handleInput}
            onFocus={()=> setActiveInput(true)}
            onBlur={handleBlur}
            onKeyDown={onKey}
        />
        {isActiveDelete && <img onClick={onClickDelete} src={closeIcons} alt="delete_text" />}
    </div>
    <ul className={isActiveInput ? 'lists-wrapper active' : 'lists-wrapper'}>
        {isFound && <li className='not-found'>city is not found</li>}
        {cityOptions.map((item, index) => 
        <li 
            key={index} 
            onClick={()=>handleSearchClick(item)} 
            onMouseEnter={() => onMouseEnter(item)}
        >
            <h3>{item.name}</h3>
            <div className='city-details'>
                {item.adminName1 && <p>{item.adminName1},</p>}
                {item.countryName && <p>{item.countryName}</p>}
            </div>
        </li>)}
    </ul>
</div>
)
}
