import React from 'react'
import { useContext } from 'react'
import Context from '../../../../helper/Context'
import { mapCodesToImage } from '../../../../helper/constants'
import DaytimeTemperature from '../../../../components/DaytimeTemperature'


export default function TwelveDays() {

  const twelveDaysData = useContext(Context)

  let arrTwelve = []
  let src

  if(twelveDaysData.weatherSixteenDays){
    arrTwelve = twelveDaysData.weatherSixteenDays.slice(0, 12)
    src = mapCodesToImage[twelveDaysData.weatherSixteenDays.weather.icon]
  }

//   if(data){
//     srcWeather = mapCodesToImage[data.weather.icon]
// }

  return (
    <div className='twelve-days'>
      {arrTwelve.map(item => <DaytimeTemperature data={item.valid_date} image={item.weather.icon} max={item.app_max_temp} min={item.app_min_temp} description={item.weather.description} />)}
    </div>
  )
}
