import React, { useState } from 'react'
import './NavigationWeatherForecast.css'

export default function NavigationWeatherForecast() {

    const [isActiveFourDays, setActiveFourDays] = useState(true)
    const [isActiveEightDays, setActiveEightDays] = useState(false)
    const [isActiveTwelveDays, setActiveTwelveDays] = useState(false)
    const [isActiveSixteenDays, setActiveSixteenDays] = useState(false)

    const onClickFourDays = () => {
        setActiveFourDays(true)
        setActiveEightDays(false)
        setActiveTwelveDays(false)
        setActiveSixteenDays(false)
    }

    const onClickEightDays = () => {
        setActiveFourDays(false)
        setActiveEightDays(true)
        setActiveTwelveDays(false)
        setActiveSixteenDays(false)
    }

    const onClickTwelveDays = () => {
        setActiveFourDays(false)
        setActiveEightDays(false)
        setActiveTwelveDays(true)
        setActiveSixteenDays(false)
    }

    const onClickSixteenDays = () => {
        setActiveFourDays(false)
        setActiveEightDays(false)
        setActiveTwelveDays(false)
        setActiveSixteenDays(true)
    }

    return (
    <nav>
        <ul className='navigation-weather-forecast'>
            <li className={isActiveFourDays && 'four-days'} onClick={onClickFourDays}>4 days</li>
            <li className={isActiveEightDays && 'eight-days'} onClick={onClickEightDays}>8 days</li>
            <li className={isActiveTwelveDays && 'twelve-days'} onClick={onClickTwelveDays}>12 days</li>
            <li className={isActiveSixteenDays && 'sixteen-days'} onClick={onClickSixteenDays}>16 days</li>
        </ul>
    </nav>
    )
}
