import React, { useContext, useEffect } from 'react'
import './WeatherForecast.css'
import NavigationWeatherForecast from './NavigationWeatherForecast/NavigationWeatherForecast'
import ContentWeatherForecast from './ContentWeatherForecast/ContentWeatherForecast'
import Context from '../../helper/Context'

export default function WeatherForecast() {

    const contextData = useContext(Context)

    useEffect(() => {
        if(contextData.latCity && contextData.lonCity){
            contextData.setLoading(true)
            fetch(`https://api.weatherbit.io/v2.0/forecast/daily?lat=${contextData.latCity}&lon=${contextData.lonCity}&key=c2d3c64087e54d018cae06444bf6a848`)
            .then(data => data.json())
            .then(data => {
                contextData.setWeatherSixteenDays(data.data)
                contextData.setLoading(false)
            })
            .catch(() => {
                contextData.setLoading(false)
                contextData.setError(true)
            })
            console.log('hhh')
        }
    }, [contextData.latCity, contextData.lonCity])

    return (
    <div className='weather-forecast'>
        <NavigationWeatherForecast />
        <ContentWeatherForecast />
    </div>
    )
}
