import { useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import './App.css';
import InputSearch from './components/Input/InputSearch';
import Header from './Header/Header';
import Weather from './Section/Weather/Weather';
import Maps from './Section/Maps/Maps';
import News from './Section/News/News';

function App() {

  const [weatherData, setWeatherData] = useState([])
  const [nameCity, setNameCity] = useState('')

  return (
    <div className="App">
      <BrowserRouter>
        <Header />
        <Routes>
          <Route path='/' element={<Weather />} />
          <Route path='/maps' element={<Maps />} />
          <Route path='/news' element={<News />} />
        </Routes>
      </BrowserRouter>
        <InputSearch weatherData={setWeatherData} nameCity={setNameCity} />
        {weatherData.map(item => <div>
          <h3>{nameCity}</h3>
          <h1>{Math.round(item.temp)} Â°C</h1>
          <p>{item.ob_time}</p>
        </div>)}
    </div>
  );
}

export default App;
