import { useRef, useState } from 'react';
import './App.css';

function App() {

  const cityRef = useRef()
  const [weatherData, setWeatherData] = useState([])
  const [cityOptions, setCityOptions] = useState([])
  const [coordinates, setCoordinates] = useState([])

  function handleInputChange(){
    if(cityRef.current.value.length > 1){
      fetch(`http://api.geonames.org/searchJSON?q=${cityRef.current.value}&maxRows=1000&username=mkskhjwssofwfjjcjw00&`)
        .then(data => data.json())
        .then(data => {
          console.log(data)
          const arrayCities = data.geonames
          .filter(city => city.name.toLowerCase().startsWith(cityRef.current.value.toLowerCase()))
          setCityOptions(arrayCities)
      })
    }
  }

  console.log(cityOptions)

  function handleSearchClick(e){
    setCoordinates(e)
    console.log(coordinates)

  }

  return (
    <div className="App">
      <input 
        ref={cityRef} 
        type="text" 
        onChange={handleInputChange} 
      />
      {cityOptions.map(item => <div onClick={handleSearchClick} style={{border: '1px solid black'}}>
        <h3>{item.name}</h3>
        <div style={{display:'flex', gap: '5px', textAlign: 'center'}}>
          <p>{item.adminName1},</p>
          <p>{item.countryName}</p>
        </div>
      </div>)}
      {/* <button onClick={handleSearchClick}>Search</button> */}
      {weatherData.map(item => <div>
        <h3>{item.city_name}</h3>
        <h1>{Math.round(item.temp)} Â°C</h1>
        <p>{item.ob_time}</p>
      </div>)}
    </div>
  );
}

export default App;
