import { useEffect, useRef, useState } from 'react';
import './App.css';

function App() {

  const cityRef = useRef()

  const [city, setCity] = useState('')
  const [coordinates, setCoordinates] = useState([])
  const [weatherData, setWeatherData] = useState([])


  function handleClick(){
    const cityName = cityRef.current.value
    // setCity()

    fetch(`http://api.geonames.org/searchJSON?q=${cityName}&maxRows=10&username=mkskhjwssofwfjjcjw00`)
      .then(data => data.json())
      .then(data => setCoordinates(data.geonames))
      .catch(() => alert('error coordinates'))

    fetch('https://api.weatherbit.io/v2.0/current?lat=49.75187&lon=31.47003&key=097dd5fffd324ebb875e47ef3fed2fa1	')
      .then(data => data.json())
      .then(data => setWeatherData(data.data))
      .catch(error => alert(error))
  }

  console.log(city)

  console.log(coordinates)

  return (
    <div className="App">
      <input ref={cityRef} type="text" />
      <button onClick={handleClick}>Search</button>
      {weatherData.map(item => <div>
        <h3>{item.city_name}</h3>
        <h1>{item.temp}</h1>
        <p>{item.ob_time}</p>
      </div>)}
    </div>
  );
}

export default App;
