import React, { useContext } from 'react'
import './Navigation.css'
import { NavLink, Outlet, useNavigate } from 'react-router-dom'
import Context from '../../helper/Context'

export default function Navigation() {

    const contextData = useContext(Context)
    const navigate = useNavigate()

    const onClick = (e) => {
        e.preventDefault()
        contextData.setLoading(true)
        fetch(`https://api.weatherbit.io/v2.0/forecast/daily?lat=${contextData.latCity}&lon=${contextData.lonCity}&key=c2d3c64087e54d018cae06444bf6a848`)
        .then(data => data.json())
        .then(data => {
            contextData.setWeatherSixteenDays(data.data)
        })
        .catch(() => {
            contextData.setLoading(false)
            contextData.setError(true)
        })
        navigate('/weather-forecast/8days')
    }

    return (
        <>
            <nav>
                <ul className='navigation'>
                    <li><NavLink to="/">Weather Now</NavLink></li>
                    <li><NavLink onClick={onClick} to="/weather-forecast">Weather Forecast</NavLink></li>
                </ul>
            </nav>
            <Outlet />
        </>
    )
}
