import { useEffect, useRef, useState } from 'react';
import './App.css';

function App() {

  const city = useRef()

  const [coordinates, setCoordinates] = useState([])
  const [cities, setCities] = useState([])

  useEffect(()=>{

    fetch('http://api.geonames.org/searchJSON?q=madrid&maxRows=10&username=mkskhjwssofwfjjcjw00')
      .then(data => data.json())
      .then(data => setCoordinates(data.geonames))
      .catch(() => alert('error coordinates'))


    fetch('https://api.weatherbit.io/v2.0/current?lat=49.75187&lon=31.47003&key=097dd5fffd324ebb875e47ef3fed2fa1	')
      .then(data => data.json())
      .then(data => setCities(data.data))
      .catch(error => alert(error))
  },[])

  console.log(coordinates)

  return (
    <div className="App">
      <input ref={city} type="text" />
      <button onClick={()=> console.log(city.current.value)}>Search</button>
      {cities.map(item => <div>
        <h3>{item.city_name}</h3>
        <h1>{item.temp}</h1>
        <p>{item.ob_time}</p>
      </div>)}
    </div>
  );
}

export default App;
