import React, { useContext } from 'react'
import './WeatherHourly.css'
import Context from '../../../helper/Context'
import HourlyTemperature from '../../../components/HourlyTemperature'

export default function WeatherHourly() {

    const arrayWeatherHourly = useContext(Context)

    let reducedArrayWeatherHourly = []

    if(arrayWeatherHourly.weatherHourly){
        reducedArrayWeatherHourly = arrayWeatherHourly.weatherHourly.slice(1,25)
    }

    return (
    <div className='weather-hourly'>
        {reducedArrayWeatherHourly.map(item => <HourlyTemperature 
            date={item.timestamp_utc} 
            image={item.weather.icon} 
            description={item.weather.description} 
            temperature={(Math.round(item.temp) > 0 ? '+' + Math.round(item.temp) : Math.round(item.temp)) + '°'}
            humidity={item.rh}
            precipitation={item.precip}
            wind={Math.round(item.wind_spd) + 'm/s'}
        />)}
    </div>
    )
}
