import React, { useEffect } from 'react'
import { useState, useRef, useContext } from 'react'
import { useLocation, useNavigate } from 'react-router-dom'
import {uniqueBy} from '../../helpers'
import Context from '../../components/Context'
import './InputSearch.css'

export default function InputSearch() {

const cityRef = useRef()
const navigate = useNavigate()
const location = useLocation()
const setData = useContext(Context)
const [cityOptions, setCityOptions] = useState([])
const [isActiveInput, setActiveInput] = useState(false)
const [typingTimer, setTypingTimer] = useState(null)


const fetchLocation = () => {
    fetch(`http://api.geonames.org/searchJSON?name=${cityRef.current.value}&maxRows=100&username=mkskhjwssofwfjjcjw00&&&`)  // lang=ru
        .then(data => data.json())
        .then(data => {
            const filteredCities = data.geonames
            .filter(filt => {
                return filt.fclName === "city, village,..." 
                && filt.fcodeName !== "section of populated place" 
                && filt.lat && filt.lng 
                && filt.name.toLowerCase().startsWith(cityRef.current.value.toLowerCase())
            })
            .sort((a,b)=> b.population - a.population)
            const uniqueFilteredCities = uniqueBy(filteredCities, (o1, o2) => o1.name === o2.name && o1.adminName1 === o2.adminName1 && o1.countryName === o2.countryName)
            setCityOptions(uniqueFilteredCities)
        })
        .catch(() => alert('Error 1'))
}

useEffect(() => {
    const timer = setInterval(() => {
        if(cityRef.current.value.length > 0){
            fetchLocation()
        }
        else{
            setCityOptions([])
        }
    },1000)

    return clearInterval(timer)

},[cityRef.current.value])

function handleInputChange(){
    setActiveInput(true)

        // fetch(`https://restcountries.com/v3.1/name/${cityOptions.countryName}?fields=flags`)
        // .then(data => data.json())
        // .then(data => console.log(data))

}

function handleSearchClick(item){
    if(location.pathname !== ''){
        fetch(`https://api.weatherbit.io/v2.0/current?lat=${item.lat}&lon=${item.lng}&key=097dd5fffd324ebb875e47ef3fed2fa1`)
        .then(response  => response.json())
        .then(response  => {
            setData.setWeatherData(response.data)
            setData.setNameCity(item.name)
            navigate('/')
            cityRef.current.value = ''
            setCityOptions([])
        })
        .catch(() => alert('Error 2'))
    }
    if(location.pathname === ''){
        fetch(`https://api.weatherbit.io/v2.0/current?lat=${item.lat}&lon=${item.lng}&key=097dd5fffd324ebb875e47ef3fed2fa1`)
        .then(response  => response.json())
        .then(response  => {
            setData.setWeatherData(response.data)
            setData.setNameCity(item.name)
            cityRef.current.value = ''
            setCityOptions([])
        })
        .catch(() => alert('Error 2'))
    }
}

console.log()

function handleBlur(){
    setTimeout(()=>{
        setActiveInput(false)
    },100)
}

return (
<div className='search-wrapper'>
    <input
        className={isActiveInput ? 'input-search active' : 'input-search'}
        placeholder='🔍︎ Search location'
        ref={cityRef} 
        type="text" 
        onChange={handleInputChange}
        onFocus={()=> setActiveInput(true)}
        onBlur={handleBlur}
    />
    <ul className={isActiveInput ? 'lists-wrapper active' : 'lists-wrapper'}>
        {cityOptions.map(item => <li onClick={()=>handleSearchClick(item)}>
            <h3>{item.name}</h3>
            <div className='city-details'>
                {item.adminName1 && <p>{item.adminName1},</p>}
                {item.countryName && <p>{item.countryName}</p>}
            </div>
        </li>)}
    </ul>
</div>
)
}
