import React from 'react'
import { useState, useRef } from 'react'
import './InputSearch.css'

export default function InputSearch(props) {

const cityRef = useRef()
const [cityOptions, setCityOptions] = useState([])
const [isActiveInput, setActiveInput] = useState(false)

function handleInputChange(){
    setActiveInput(true)
    fetch(`http://api.geonames.org/searchJSON?name=${cityRef.current.value}&maxRows=100&username=mkskhjwssofwfjjcjw00&&&`)  // lang=ru
    .then(data => data.json())
    .then(data => {
        const filteredCities = data.geonames
        .filter(filt => filt.fclName === "city, village,..." && filt.fcodeName !== "section of populated place")
        .filter(city => city.name.toLowerCase().includes(cityRef.current.value.toLowerCase()))
        setCityOptions(filteredCities)
    })
}

console.log(isActiveInput)

function handleSearchClick(item){
    fetch(`https://api.weatherbit.io/v2.0/current?lat=${item.lat}&lon=${item.lng}&key=097dd5fffd324ebb875e47ef3fed2fa1`)
    .then(response  => response.json())
    .then(response  => {
        props.weatherData(response.data)
        cityRef.current.value = ''
        setCityOptions([])
    })
    .catch(error => alert(error))
}

return (
<div className='input-search'>
    <input
        placeholder='🔍︎ Search location'
        ref={cityRef} 
        type="text" 
        onChange={handleInputChange} 
    />
    <ul className='lists-wrapper'>
        {cityOptions.map(item => <li onClick={()=>handleSearchClick(item)}>
            <h3>{item.name}</h3>
            <div className='city-details'>
                {item.adminName1 && <p>{item.adminName1},</p>}
                {item.countryName && <p>{item.countryName}</p>}
            </div>
        </li>)}
    </ul>
</div>
)
}
