const getCurrentWeather = (lat, lon, setWeatherData, setLoading, setError) => {
        setLoading(true)
        fetch(`https://api.weatherbit.io/v2.0/current?lat=${lat}&lon=${lon}&key=c2d3c64087e54d018cae06444bf6a848`)
        .then(data => data.json())
        .then(data => {
            setWeatherData(data.data)
            setLoading(false)
        })
        .catch(() => {
            setLoading(false)
            setError(true)
        })
}

