const getCurrentCity = (lat, lon, setNameCity, setLoading, setError) => {
    fetch(`https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lon}&accept-language=en`)
        .then(data => data.json())
        .then(data => {
            setNameCity(data.address.city)
            setLoading(false)
        })
        .catch(() => {
            setLoading(false)
            setError(true)
        })
}

export {getCurrentCity}
