    <div className='weather-wrapper'>
        {arrData.weatherData.map((item, index) => <div key={index}>
          <div className="weather">
            <h1>{item.temp > 0 ? `+${Math.round(item.temp)}` : Math.round(item.temp)}Â°</h1>
            <img src="" alt="" />
          </div>
          
          <h4>{arrData.nameCity}</h4>: <h4>{item.weather.description}</h4>
          <p>{item.ob_time}</p>
        </div>)}
    </div>
    )
}
