import React, { useContext } from 'react'
import './Navigation.css'
import { NavLink, Outlet, useNavigate } from 'react-router-dom'
import Context from '../../helper/Context'

export default function Navigation() {

    const navigate = useNavigate()
    const weatherContext = useContext(Context)

    const onClick = (e) => {
        e.preventDefault()
        navigate('/weather-forecast/8days', {state: {lat: weatherContext.latCity, lon: weatherContext.lonCity ,city: weatherContext.nameCity.toLowerCase()}})
    }

    return (
        <>
            <nav>
                <ul className='navigation'>
                    <li><NavLink to={`/?lat=${weatherContext.latCity}&lon=${weatherContext.lonCity}&city=${weatherContext.nameCity.toLowerCase()}`}>Weather Now</NavLink></li>
                    <li><NavLink onClick={onClick} to="/weather-forecast">Weather Forecast</NavLink></li>
                </ul>
            </nav>
            <Outlet />
        </>
    )
}
